import React, { Component } from "react";
import { withRouter, Switch, Route } from "react-router-dom";
import { connect } from "react-redux";
import ProtectedRoute from "./components/protected-route/protectedroute";

//Common Screens
import Dashboard from "./screens/dashboard";
import LoginScreen from "./screens/loginscreen";
import EtatDuCompte from "./screens/etatDuCompte";
import Achats from "./screens/achats";
import Ventes from "./screens/ventes";
import Stock from "./screens/stock";
import Solicitude from "./screens/solicitude";
//Caissier Screens
import ValidateSolicitude from "./screens/validateSolicitude";
import ValidateCommande from "./screens/validateCommande";
import RealiserPaiement from "./screens/realiserPaiement";
import VoirTransactions from "./screens/voirTransactions";
//Vendeur Screens
import Commande from "./screens/commande";
import Produits from "./screens/produits";
import VendorRapports from "./screens/vendorRapports";
//Comite Screens
import BanqueRapports from "./screens/banqueRapports";
import BeneficesRapports from "./screens/beneficesRapports";
import SolicitudeRapports from "./screens/solicitudeRapports";
//Contable Screens
import AccountingRapports from "./screens/accountingRapports";
import Banque from "./screens/banque";
//Assistance Screens
import AssistanceRapports from "./screens/assistanceRapports";
import Fournisseurs from "./screens/fournisseurs";
//Directeur Screens
import DirecteurRapports from "./screens/directeurRapports";
import Utilisateurs from "./screens/utilisateurs";
import Salaire from "./screens/salaire";
import ComiteMonitor from "./screens/comiteMonitor";

class App extends Component {
  render() {
    const { isLoggedIn } = this.props.user;
    return (
      <div id="App" className="App">
        <Switch>
          <Route exact path={"/"} component={LoginScreen} />
          <Route path={"/login"} component={LoginScreen} />
          <ProtectedRoute
            isLoggedIn={isLoggedIn}
            path={"/dashboard"}
            component={Dashboard}
          />
          <ProtectedRoute
            isLoggedIn={isLoggedIn}
            path={"/validate_solicitude"}
            component={ValidateSolicitude}
          />
          <ProtectedRoute
            isLoggedIn={isLoggedIn}
            path={"/validate_order"}
            component={ValidateCommande}
          />
          <ProtectedRoute
            isLoggedIn={isLoggedIn}
            path={"/payment"}
            component={RealiserPaiement}
          />
          <ProtectedRoute
            isLoggedIn={isLoggedIn}
            path={"/view_transactions"}
            component={VoirTransactions}
          />
          <ProtectedRoute
            isLoggedIn={isLoggedIn}
            path={"/orders"}
            component={Commande}
          />
          <ProtectedRoute
            isLoggedIn={isLoggedIn}
            path={"/products"}
            component={Produits}
          />
          <ProtectedRoute
            isLoggedIn={isLoggedIn}
            path={"/vendor_reports"}
            component={VendorRapports}
          />
          <ProtectedRoute
            isLoggedIn={isLoggedIn}
            path={"/banking_reports"}
            component={BanqueRapports}
          />
          <ProtectedRoute
            isLoggedIn={isLoggedIn}
            path={"/benefits_reports"}
            component={BeneficesRapports}
          />
          <ProtectedRoute
            isLoggedIn={isLoggedIn}
            path={"/solicitude_reports"}
            component={SolicitudeRapports}
          />
          <ProtectedRoute
            isLoggedIn={isLoggedIn}
            path={"/cash_status"}
            component={EtatDuCompte}
          />
          <ProtectedRoute
            isLoggedIn={isLoggedIn}
            path={"/buys"}
            component={Achats}
          />
          <ProtectedRoute
            isLoggedIn={isLoggedIn}
            path={"/sales"}
            component={Ventes}
          />
          <ProtectedRoute
            isLoggedIn={isLoggedIn}
            path={"/banking"}
            component={Banque}
          />
          <ProtectedRoute
            isLoggedIn={isLoggedIn}
            path={"/accounting_reports"}
            component={AccountingRapports}
          />
          <ProtectedRoute
            isLoggedIn={isLoggedIn}
            path={"/warehouse"}
            component={Stock}
          />
          <ProtectedRoute
            isLoggedIn={isLoggedIn}
            path={"/suppliers"}
            component={Fournisseurs}
          />
          <ProtectedRoute
            isLoggedIn={isLoggedIn}
            path={"/assistance_reports"}
            component={AssistanceRapports}
          />
          <ProtectedRoute
            isLoggedIn={isLoggedIn}
            path={"/director_reports"}
            component={DirecteurRapports}
          />
          <ProtectedRoute
            isLoggedIn={isLoggedIn}
            path={"/users"}
            component={Utilisateurs}
          />
          <ProtectedRoute
            isLoggedIn={isLoggedIn}
            path={"/salary"}
            component={Salaire}
          />
          <ProtectedRoute
            isLoggedIn={isLoggedIn}
            path={"/solicitude"}
            component={Solicitude}
          />
          <ProtectedRoute
            isLoggedIn={isLoggedIn}
            path={"/comite_monitor"}
            component={ComiteMonitor}
          />
        </Switch>
      </div>
    );
  }
}

function mapStateToProps({ user }) {
  return { user };
}

export default withRouter(connect(mapStateToProps)(App));
