import { NAVIGATE, PAGE_INDEX, RESET_NAVIGATE } from "../actions/navigate";

export default function navigation(
  state = { index: 0, route: "", navigate: false },
  action
) {
  const { type, payload } = action;
  switch (type) {
    case NAVIGATE:
      return {
        ...state,
        index: payload.index,
        route: payload.route,
        navigate: payload.navigate
      };

    case RESET_NAVIGATE:
      return { ...state, navigate: false };

    case PAGE_INDEX:
      return { ...state, index: payload.index }

    default:
      return state;
  }
}
