export const NAVIGATE = "NAVIGATE";
export const RESET_NAVIGATE = "RESET_NAVIGATE";
export const PAGE_INDEX = "PAGE_INDEX";

function navigation(type, index, route) {
  return { type, payload: { index, route, navigate: true } };
}

function resetNavigation() {
  return { type: RESET_NAVIGATE };
}

function setPageIndex(index) {
  return { type: PAGE_INDEX, payload: { index } };
}

export function menuNavigation(index, route) {
  return dispatch => {
    dispatch(navigation(NAVIGATE, parseInt(index), route));
  };
}

export function endNavigation() {
  return dispatch => {
    return dispatch(resetNavigation());
  };
}

export function navigate(route) {
  return dispatch => {
    const currentWindow = window.location.pathname;
    if (currentWindow !== route) {
      dispatch(navigation(NAVIGATE, 0, route));
    }
  };
}

export function getPageIndex(index) {
  return dispatch => {
    dispatch(setPageIndex(index));
  };
}
