import React, { Component } from "react";
import { isArray } from "lodash";

//Internal Components
import SidebarMenu from "../../components/sidebar-menu/sidebar-menu";
import TopBar from "../../components/topbar/topbar";

//Styles
import "./custom-menu.css";

class CustomMenu extends Component {
  createMenuOptions = userType => {
    let Options = [];
    switch (userType) {
      case "vendeur":
        Options = [
          {
            name: "orders",
            icon: "clipboard list",
            label: "Commande",
            index: 0
          },
          {
            name: "products",
            icon: "box",
            label: "Produits",
            index: 1
          },
          {
            name: "vendor_reports",
            icon: "chart bar outline",
            label: "Rapports",
            index: 2
          }
        ];
        break;
      case "caissier":
        Options = [
          {
            name: "validate_solicitude",
            icon: "check square",
            label: "Valider Solicitude",
            index: 0
          },
          {
            name: "validate_order",
            icon: "clipboard check",
            label: "Valider Commande",
            index: 1
          },
          {
            name: "payment",
            icon: "payment",
            label: "Realiser Paiement",
            index: 2
          },
          {
            name: "view_transactions",
            icon: "handshake outline",
            label: "Voir Transactions",
            index: 3
          }
        ];
        break;
      case "directeur":
        Options = [
          {
            name: "director_reports",
            icon: "chart line",
            label: "Rapports",
            index: 0
          },
          {
            name: "users",
            icon: "users",
            label: "Utilisateurs",
            index: 1
          },
          {
            name: "salary",
            icon: "money bill alternate outline",
            label: "Augmenter le Salaire",
            index: 2
          },
          {
            name: "cash_status",
            icon: "money",
            label: "Etat du Compte",
            index: 3
          },
          {
            name: "buys",
            icon: "cart",
            label: "Reviser Achats",
            index: 4
          },
          {
            name: "sales",
            icon: "tags",
            label: "Reviser Ventes",
            index: 5
          },
          {
            name: "solicitude",
            icon: "hand lizard outline",
            label: "Solicitude",
            index: 6
          },
          {
            name: "warehouse",
            icon: "archive",
            label: "Stock",
            index: 7
          },
          {
            name: "comite_monitor",
            icon: "sign-in",
            label: "Rentrez de Comite",
            index: 8
          }
        ];
        break;
      case "comite":
        Options = [
          {
            name: "benefits_reports",
            icon: "chart area",
            label: "Rapports du Benefices",
            index: 0
          },
          {
            name: "banking_reports",
            icon: "chart line",
            label: "Rapports du Banque",
            index: 1
          },
          {
            name: "solicitude_reports",
            icon: "chart bar",
            label: "Rapports de Solicitude",
            index: 2
          }
        ];
        break;
      case "contable":
        Options = [
          {
            name: "cash_status",
            icon: "money",
            label: "Etat du Compte",
            index: 0
          },
          {
            name: "buys",
            icon: "cart",
            label: "Achats",
            index: 1
          },
          {
            name: "sales",
            icon: "tags",
            label: "Ventes",
            index: 2
          },
          {
            name: "banking",
            icon: "lock",
            label: "Banque",
            index: 3
          },
          {
            name: "accounting_reports",
            icon: "chart bar outline",
            label: "Rapports",
            index: 4
          }
        ];
        break;
      case "assistance":
        Options = [
          {
            name: "warehouse",
            icon: "archive",
            label: "Stock",
            index: 0
          },
          {
            name: "suppliers",
            icon: "users",
            label: "Fournisseurs",
            index: 1
          },
          {
            name: "buys",
            icon: "cart",
            label: "Reviser Achats",
            index: 2
          },
          {
            name: "assistance_reports",
            icon: "chart bar outline",
            label: "Rapports",
            index: 3
          }
        ];
        break;
    }
    return Options;
  };

  renderChildren = children => {
    if (isArray(children)) {
      const items = children.map((child, index) => {
        return (
          <div className="contentContainer" key={index}>
            {child}
          </div>
        );
      });
      return items;
    } else {
      return <div className="contentContainer">{children}</div>;
    }
  };

  render() {
    const { children, userType, screenName } = this.props;
    const menuOptions = this.createMenuOptions("directeur");
    return (
      <div className="custom-menu-container">
        <div className="sidebar-box">
          <SidebarMenu data={menuOptions} />
        </div>
        <div className="right-box">
          <TopBar screenName={screenName} />
          <div className="custom-menu-sidebar">
            {this.renderChildren(children)}
          </div>
        </div>
      </div>
    );
  }
}

export default CustomMenu;
