import React, { PureComponent } from "react";
import { Redirect } from "react-router-dom";
import { connect } from "react-redux";
import { Form, Button, Card, Input, Dimmer, Loader, Message, Icon, Divider, TextArea } from "semantic-ui-react";

//Components
import PasswordInput from "../password-input/passwordinput";

//Logic
// import { register } from "../../redux/actions/users";

//Styles
import styles from "./registerform.module.css";

class RegisterForm extends PureComponent {
	constructor(props) {
		super(props);

		this.state = {
			nom: "",
			prenom: "",
			email: "",
			tel: "",
			etatCivil: "",
			salaire: "",
			role: "",
			adresse: "",
			dateDeNaissance: "",
			motDePasse: "",
			nomError: false,
			prenomError: false,
			emailError: false,
			salaireError: false,
			roleError: false,
			motDePasseError: false
		};
	}

	handleInputError = event => {
		const { name, value } = event.target;
		let regex = value.match(new RegExp("^[_A-z0-9 ]*((-|s)*[_A-z0-9])*$"));
		if (name === "branchName") {
			if (value.length === 0 || regex === null) {
				return this.setState({
					branchNameError: true,
					formError: true
				});
			} else {
				return this.setState({
					branchNameError: false,
					formError: false
				});
			}
		}

		if (name === "branchMail") {
			let mail = value.match(
				new RegExp(
					"^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9](?:[a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?(?:.[a-zA-Z0-9](?:[a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?)*$"
				)
			);

			if (value.length === 0 || mail === null) {
				return this.setState({
					branchMailError: true,
					formError: true
				});
			} else {
				return this.setState({
					branchMailError: false,
					formError: false
				});
			}
		}

		if (name === "branchPhone") {
			let phone = value.match(new RegExp("^[0-9]+$"));
			if (value.length === 0 || phone === null) {
				return this.setState({
					branchPhoneError: true,
					formError: true
				});
			} else {
				return this.setState({
					branchPhoneError: false,
					formError: false
				});
			}
		}

		if (name === "branchAdress") {
			if (value.length === 0) {
				return this.setState({
					branchAdressError: true,
					formError: true
				});
			} else {
				return this.setState({
					branchAdressError: false,
					formError: false
				});
			}
		}
	};

	handleSubmit = event => {
		event.preventDefault();
		const {
			email,
			phoneNumber,
			personalId,
			password,
			names,
			lastNames,
			emailError,
			phoneNumberError,
			personalIdError,
			namesError,
			lastNamesError
		} = this.state;

		if (
			email === "" ||
			phoneNumber === "" ||
			personalId === "" ||
			password === "" ||
			names === "" ||
			lastNames === "" ||
			emailError ||
			phoneNumberError ||
			personalIdError ||
			namesError ||
			lastNamesError
		) {
			console.log(this.state);
			return this.setState({
				isModalOpen: true
			});
		}
		// this.props.dispatch(register(names, lastNames, email, password, personalId, phoneNumber, companyID));
	};

	handleInputOnChange = event => {
		const { name, value } = event.target;
		this.setState({ [name]: value });
	};

	renderMessages = field => {
		let list = [];
		let error = field.concat("Error");
		switch (field) {
			case "branchName":
				list = [
					"El nombre de la sucursal no puede estar vacio",
					"El nombre de la sucursal no puede tener Caracteres especiales"
				];
				break;
			case "branchMail":
				list = ["El email de la sucursal no puede estar vacio", "No es un correo electronico valido"];
				break;
			case "branchPhone":
				list = ["El telefono no puede estar vacio", "El telefono solo contiene numeros"];
				break;
			case "branchCountry":
				list = ["El pais no puede estar vacio"];
				break;
			case "branchRegions":
				list = ["La Region no puede estar vacia"];
				break;
			case "branchState":
				list = ["La provincia no puede estar vacia"];
				break;
			case "branchCity":
				list = ["La ciudad no puede estar vacia"];
				break;
			case "branchZone":
				list = ["El sector no puede estar vacio"];
				break;
			case "branchAdress":
				list = ["La direccion no puede estar vacia"];
				break;
			default:
				list = [];
		}
		return (
			<Message style={this.state[error] ? { display: "block" } : { display: "none" }} error size="small" list={list} />
		);
	};

	render() {
		if (this.props.navigation.navigate === true && window.location.pathname !== this.props.navigation.route) {
			return <Redirect push to={this.props.navigation.route} />;
		}

		let title = "Entrain de Ajouter une Commande";
		let label = "Ajouter";
		if (this.state.edit) {
			title = "Entrain de Modifier le Commande";
			label = "Modifier";
		} else if (this.state.view) {
			title = "Entrain de Voir le Commande";
		}

		return (
			<Dimmer.Dimmable dimmed={this.props.user.isFetching || this.state.isModalOpen}>
				<Dimmer
					verticalAlign="top"
					page
					active={this.props.user.isFetching || this.state.isModalOpen}
					onClickOutside={this.handleModalClose}
				>
					{this.state.isModalOpen ? (
						<Message className="dimmerMargin" negative>
							<Message.Item className="noStyleList">
								<Icon name="warning sign" size="huge" />
							</Message.Item>
							<Message.Content>
								"Revise y complete correctamente los campos con las informaciones de lugar"
							</Message.Content>
						</Message>
					) : (
						<Loader size="huge">Loading...</Loader>
					)}
				</Dimmer>
				<Card fluid centered className={styles.boxContainerWide}>
					<Card.Content>
						<Card.Header className="font font-18" textAlign="center">
							{title}
						</Card.Header>
						<div className={styles.basicFormContainer}>
							<div className={styles.basicFormSecond}>
								<Divider horizontal>Datos Requeridos</Divider>
								<Form size="small">
									<Form.Group grouped>
										<Form.Field required>
											<label className={styles.basicFormSpacing}>Prenom</label>
											<Input
												disabled={this.state.view}
												icon="user"
												iconPosition="left"
												placeholder="Prenom"
												name="prenom"
												onChange={this.handleChange}
												value={this.state.prenom}
												onKeyUp={this.handleInputError}
												onBlur={this.state.prenomError ? null : this.handleInputError}
											/>
											{this.renderMessages("prenom")}
										</Form.Field>
										<Form.Field required>
											<label className={styles.basicFormSpacing}>Nom</label>
											<Input
												disabled={this.state.view}
												icon="user"
												iconPosition="left"
												placeholder="Nom"
												name="nom"
												onChange={this.handleChange}
												value={this.state.nom}
												onKeyUp={this.handleInputError}
												onBlur={this.state.nomError ? null : this.handleInputError}
											/>
											{this.renderMessages("nom")}
										</Form.Field>
										<Form.Field required>
											<label className={styles.basicFormSpacing}>Email</label>
											<Input
												disabled={this.state.view}
												icon="mail"
												iconPosition="left"
												placeholder="Email"
												name="email"
												onChange={this.handleChange}
												value={this.state.email}
												onKeyUp={this.handleInputError}
												onBlur={this.state.emailError ? null : this.handleInputError}
											/>
											{this.renderMessages("email")}
										</Form.Field>
										<Form.Field required>
											<label className={styles.basicFormSpacing}>Role</label>
											<Input
												disabled={this.state.view}
												icon="mail"
												iconPosition="left"
												placeholder="Email"
												name="email"
												onChange={this.handleChange}
												value={this.state.email}
												onKeyUp={this.handleInputError}
												onBlur={this.state.emailError ? null : this.handleInputError}
											/>
											{this.renderMessages("email")}
										</Form.Field>
										<Form.Field required>
											<label className={styles.basicFormSpacing}>Salaire</label>
											<Input
												disabled={this.state.view}
												icon="money"
												iconPosition="left"
												placeholder="Salaire"
												name="salaire"
												onChange={this.handleChange}
												value={this.state.salaire}
												onKeyUp={this.handleInputError}
												onBlur={this.state.salaireError ? null : this.handleInputError}
											/>
											{this.renderMessages("salaire")}
										</Form.Field>
										<Form.Field required>
											<label className={styles.basicFormSpacing}>Mot De Passe</label>
											<PasswordInput
												disabled={this.state.view}
												placeholder="Mot de Passe"
												name="motDePasse"
												value={this.state.motDePasse}
												onChange={this.handleInputOnChange}
												onBlur={this.motDePasseError ? null : this.handleInputError}
												onKeyUp={this.handleInputError}
											/>
											{this.renderMessages("motDePasse")}
										</Form.Field>
									</Form.Group>
								</Form>
							</div>
							<div className={styles.basicFormSecond}>
								<Divider horizontal>Datos Personales</Divider>
								<Form>
									<Form.Group grouped>
										<Form.Field>
											<label className={styles.basicFormSpacing}>Telephone</label>
											<Input
												disabled={this.state.view}
												icon="phone"
												iconPosition="left"
												placeholder="Telephone"
												name="tel"
												onChange={this.handleChange}
												value={this.state.tel}
											/>
										</Form.Field>
										<Form.Field>
											<label className={styles.basicFormSpacing}>Date de Naissance</label>
											<Input
												disabled={this.state.view}
												icon="phone"
												iconPosition="left"
												placeholder="Date de Naissance"
												name=""
												onChange={this.handleChange}
												value={this.state.tel}
											/>
										</Form.Field>
										<Form.Field>
											<label className={styles.basicFormSpacing}>Etat Civil</label>
											<Input
												disabled={this.state.view}
												icon="phone"
												iconPosition="left"
												placeholder="Etat Civil"
												name="etatCivil"
												onChange={this.handleChange}
												value={this.state.etatCivil}
											/>
										</Form.Field>
										<Form.Field>
											<label className={styles.basicFormSpacing}>Adresse</label>
											<TextArea
												size="small"
												disabled={this.state.view}
												name="adresse"
												onChange={this.handleChange}
												rows={4}
												placeholder="Adresse"
												value={this.state.adresse}
											/>
										</Form.Field>
									</Form.Group>
								</Form>
							</div>
						</div>
						<div style={this.state.view ? { display: "none" } : { display: "block" }}>
							<Button fluid size="small" color="teal" content={label} onClick={this.handleSubmit} />
						</div>
					</Card.Content>
				</Card>
			</Dimmer.Dimmable>
		);
	}
}

function mapStateToProps({ user, navigation }) {
	return { user, navigation };
}

export default connect(mapStateToProps)(RegisterForm);
