import api from "../../api";

export const GET_COMPTES = "GET_COMPTES";
export const GET_COMPTES_SUCCESS = "GET_COMPTES_SUCCESS";
export const GET_COMPTES_FAIL = "GET_COMPTES_FAIL";

function requestBegin(type) {
	return { type };
}

function requestSuccess(type, data, message) {
	return { type, payload: { data, message } };
}

function requestFail(type, message) {
	return { type, payload: { message } };
}

export function getComptes() {
	return dispatch => {
		dispatch(requestBegin(GET_COMPTES));
		return api
			.requestGET("/caisseTransactions")
			.then(objResponse => {
				if (objResponse.data.success) {
					dispatch(requestSuccess(GET_COMPTES_SUCCESS, objResponse.data.data, objResponse.data.message));
				} else {
					dispatch(requestFail(GET_COMPTES_FAIL, objResponse.data.message));
				}
			})
			.catch(objError => {
				dispatch(requestFail(GET_COMPTES_FAIL, objError.response));
			});
	};
}
